const res = await request
  .post("/users/register")
  .set("Content-Type", "application/json")
  .send(user);
// Debug: mostrar corpo e status da resposta
console.log("REGISTER RESPONSE STATUS:", res.status);
console.log("REGISTER RESPONSE BODY:", res.body);
expect(res.status).to.be.oneOf([200, 201]);
expect(res.body).to.have.property("username", user.username);

const res = await request
  .post("/users/login")
  .set("Content-Type", "application/json")
  .send({
    username: user.username,
    password: user.password
  });
// Debug: mostrar corpo e status da resposta
console.log("LOGIN RESPONSE STATUS:", res.status);
console.log("LOGIN RESPONSE BODY:", res.body);
expect(res.status).to.equal(200);
expect(res.body).to.have.property("token");
process.env.TEST_TOKEN = res.body.token;

const token = process.env.TEST_TOKEN;
const logData = {
  date: new Date().toISOString(),
  treatment: "hidratação",
  feedback: "Muito bom!"
};
const res = await request
  .post("/logs/add")
  .set("Authorization", `Bearer ${token}`)
  .send(logData);
console.log("LOG ADD RESPONSE STATUS:", res.status);
console.log("LOG ADD RESPONSE BODY:", res.body);
expect(res.status).to.be.oneOf([200, 201]);
expect(res.body).to.have.property("date");
expect(res.body).to.have.property("treatment");

const token = process.env.TEST_TOKEN;
const res = await request
  .get("/logs/list")
  .set("Authorization", `Bearer ${token}`);
expect(res.status).to.equal(200);
expect(res.body).to.be.an("array");
